import React, { Component } from "react";
import PokemonList from "../pokemon/PokemonList";
import Select from "react-select";
import axios from "axios";

const attackOpt = [
  { label: "high", value: 0 - 40 },
  { label: "medium", value: 41 - 70 },
  { label: "high", value: 71 - 100 }
];

export default class Dashboard extends Component {
  state = {
    url: "https://pokeapi.co/api/v2/pokemon/",
    pokemon: null,
    input: "",
    sortBy: "pokemonIndex",
    sequence: "asc",
    attack: ""
  };

  async componentDidMount() {
    const res = await axios.get(this.state.url);
    this.setState({ pokemon: res.data["results"] });
  }

  onChange = e => {
    this.setState({ input: e.target.value });
  };

  handleNameChecked = e => {
    this.setState({ sortBy: e.target.value });
    console.log("name", this.state.sequence, this.state.sortBy);
    if (this.state.sequence === "asc") {
      this.setState({
        pokemon: this.state.pokemon.sort((a, b) => (a.name < b.name ? -1 : 1))
      });
    } else {
      this.setState({
        pokemon: this.state.pokemon.sort((a, b) => (a.name > b.name ? -1 : 1))
      });
    }
  };

  handleIndexChecked = e => {
    axios.get(this.state.url).then(res => {
      this.setState({ pokemon: res.data["results"] });
    });
    this.setState({ sortBy: e.target.value });
    console.log("index", this.state.sequence, this.state.sortBy);
    if (this.state.sequence === "asc") {
      // let list = this.state.pokemon;
      // console.log("list", list);
      // this.setState({ pokemon: list });
      this.setState({
        pokemon: this.state.pokemon.sort((a, b) =>
          parseInt(a.pokemonIndex) < parseInt(b.pokemonIndex) ? -1 : 1
        )
      });
      this.setState(this.s);
    } else {
      let reversedList = this.state.pokemon;
      reversedList.reverse();
      console.log("reverseList", reversedList);
      this.setState({ pokemon: reversedList });
      // this.setState({
      //   pokemon: this.state.pokemon.sort((a, b) =>
      //     parseInt(a.pokemonIndex) > parseInt(b.pokemonIndex) ? -1 : 1
      //   )
      // });
    }
  };

  handleAscendChecked = e => {
    this.setState({ sequence: e.target.value });
    console.log("ascend", this.state.sortBy);
    if (this.state.sortBy === "name") {
      this.setState({
        pokemon: this.state.pokemon.sort((a, b) => (a.name < b.name ? -1 : 1))
      });
    } else {
      this.setState({
        pokemon: this.state.pokemon.sort((a, b) =>
          parseInt(a.pokemonIndex) < parseInt(b.pokemonIndex) ? -1 : 1
        )
      });
    }
  };

  handleDescendChecked = e => {
    this.setState({ sequence: e.target.value });
    console.log("descend", this.state.sortBy);
    if (this.state.sortBy === "pokemonIndex") {
      // this.setState({
      //   pokemon: this.state.pokemon.sort((a, b) =>
      //     parseInt(a.pokemonIndex) > parseInt(b.pokemonIndex) ? -1 : 1
      //   )
      // });
      let reversedList = this.state.pokemon;
      reversedList.reverse();
      console.log("reverseList", reversedList);
      this.setState({ pokemon: reversedList });
    } else {
      this.setState({
        pokemon: this.state.pokemon.sort((a, b) => (a.name > b.name ? -1 : 1))
      });
    }
  };

  render() {
    return (
      <React.Fragment>
        <div className="row">
          <div className="col-md-4" />
          <p />
          <input
            className="form-control form-control-lg"
            type="text"
            name="value"
            placeholder="Pokemon name here!"
            value={this.state.value}
            onChange={this.onChange}
          />
        </div>
        <p />
        <div className="row">
          <div className="col-md-6">
            <input
              type="radio"
              value="name"
              checked={this.state.sortBy === "name"}
              onChange={this.handleNameChecked}
            />
            <span className="badge badge-info badge-pill mr-1">name</span>
          </div>
          <div className="col-md-6">
            <input
              type="radio"
              value="pokemonIndex"
              checked={this.state.sortBy === "pokemonIndex"}
              onChange={this.handleIndexChecked}
            />
            <span className="badge badge-info badge-pill mr-1">index</span>
          </div>
        </div>
        <p />
        <div className="row">
          <div className="col-md-6">
            <input
              type="radio"
              value="asc"
              checked={this.state.sequence === "asc"}
              onChange={this.handleAscendChecked}
            />
            <span className="badge badge-info badge-pill mr-1">ascending</span>
          </div>
          <div className="col-md-6">
            <input
              type="radio"
              value="desc"
              checked={this.state.sequence === "desc"}
              onChange={this.handleDescendChecked}
            />
            <span className="badge badge-info badge-pill mr-1">descending</span>
          </div>
        </div>
        <p />
        <div className="row">
          <div className="col-md-4">
            <Select options={attackOpt} />
          </div>
        </div>
        <p />
        <div className="row">
          <div className="col">
            <PokemonList
              url={this.state.url}
              pokemon={this.state.pokemon}
              input={this.state.input}
              sortBy={this.state.sortBy}
              sequence={this.state.sequence}
            />
          </div>
        </div>
      </React.Fragment>
    );
  }
}
