import React, { Component } from "react";
// import PokemonList from "../pokemon/PokemonList";
// import SearchBar from "./SearchBar";
import axios from "axios";
import PokemonCard from "../pokemon/PokemonCard";

export default class Dashboard extends Component {
  state = {
    url: "https://pokeapi.co/api/v2/pokemon/",
    pokemons: null,
    input: ""
  };

  //   onChange = e => this.setState({ input: e.target.value });

  async componentDidMount() {
    const res = await axios.get(this.state.url);
    this.setState({ pokemons: res.data["results"] });
  }

  handleUserInput = e => {
    const input = e.target.value;
    console.log("input", input);
    this.setState({ input });
    const filteredPokemons = this.state.pokemons.filter(pokemon => {
      return pokemon.name.toLowerCase().startsWith(input.toLowerCase()) !== -1;
    });
    this.setState({ pokemons: filteredPokemons });
  };

  render() {
    const { value, pokemons } = this.state;
    // const filteredPokemons = pokemons.filter(pokemon => {
    //   return pokemon.name.toLowerCase().indexOf(value.toLowerCase()) !== -1;
    // });
    return (
      <React.Fragment>
        <div className="row">
          <div className="col-md-4" />
          <input
            style={{ flex: "10", padding: "5px" }}
            type="text"
            name="value"
            placeholder="Pokemon name here!"
            value={value}
            onChange={e => this.handleUserInput(e)}
          />
        </div>
        <p />
        <div className="row">
          <div className="col">
            {pokemons ? (
              <div className="row">
                {pokemons.map(pokemon => (
                  <PokemonCard
                    key={pokemon.name}
                    name={pokemon.name}
                    url={pokemon.url}
                  />
                ))}
              </div>
            ) : (
              <h1>Loading Pokemon</h1>
            )}
          </div>
        </div>
      </React.Fragment>
    );
  }
}
